function random(o){return o?(o="0."+Math.sin(o).toString().slice(6),Number(o)):Math.random()}function move(o,n=0,t=0){o.style.transform=`translate(${n}px, ${t}px)`}function randomMoveCards(o){o.forEach((o,n)=>{var t=null,e=4*(2*random(t)-1),t=2*random(t)-1;move(o,e.toFixed(3),t.toFixed(3))})}function randomMoveTitle(o){o.forEach((o,n)=>{var t="calc("+random().toFixed(2)+" * (100% - "+o.clientHeight+"px))";o.style.marginTop=t})}function onload(){randomMoveTitle(document.querySelectorAll("#card-title > div"))}document.addEventListener("DOMContentLoaded",onload);