"use strict";function random(n){return n?(n="0."+Math.sin(n).toString().slice(6),Number(n)):Math.random()}function move(n,o=0,t=0){n.style.transform=`translate(${o}px, ${t}px)`}function randomMoveCards(n){n.forEach(function(n,o){var t=null,e=4*(2*random(t)-1),t=2*random(t)-1;move(n,e.toFixed(3),t.toFixed(3))})}function randomMoveTitle(n){n.forEach(function(n,o){var t="calc("+random().toFixed(2)+" * (100% - "+n.clientHeight+"px))";n.style.marginTop=t})}function onload(){randomMoveTitle(document.querySelectorAll("#card-title > div"))}document.addEventListener("DOMContentLoaded",onload);